import unittest from Poo import ClimaSemanal
class TestClimaSemanal(unittest.TestCase):
def test_calcular_promedio_empty_returns_none(self): c = ClimaSemanal() # No se agregan temperaturas self.assertIsNone(c.calcular_promedio())
def test_calcular_promedio_values(self): c = ClimaSemanal() # Rellenamos manualmente la lista de temperaturas c.temperaturas = [10, 20, 30, 40, 50, 60, 70] expected = sum(c.temperaturas) / len(c.temperaturas) self.assertAlmostEqual(c.calcular_promedio(), expected)
def test_calcular_promedio_with_floats(self): c = ClimaSemanal() c.temperaturas = [23.5, 22.0, 19.75, 21.25, 20.0, 18.5, 24.0] expected = sum(c.temperaturas) / len(c.temperaturas) self.assertAlmostEqual(c.calcular_promedio(), expected)
if __name__ == '__main__': unittest.main()

import unittest from Poo import ClimaSemanal
class TestClimaSemanal(unittest.TestCase):
def test_calcular_promedio_empty_returns_none(self): c = ClimaSemanal() # No se agregan temperaturas self.assertIsNone(c.calcular_promedio())
def test_calcular_promedio_values(self): c = ClimaSemanal() # Rellenamos manualmente la lista de temperaturas c.temperaturas = [10, 20, 30, 40, 50, 60, 70] expected = sum(c.temperaturas) / len(c.temperaturas) self.assertAlmostEqual(c.calcular_promedio(), expected)
def test_calcular_promedio_with_floats(self): c = ClimaSemanal() c.temperaturas = [23.5, 22.0, 19.75, 21.25, 20.0, 18.5, 24.0] expected = sum(c.temperaturas) / len(c.temperaturas) self.assertAlmostEqual(c.calcular_promedio(), expected)
if __name__ == '__main__': unittest.main()
